onArrival.$code:
if (Q.month_actions > 0) {
    Q.month_actions -= 1;
}
if (Q.last_advisor_action) {
    Q.advisor_action_timer = 0;
    Q.last_advisor_action = 0;
}
if (Q.last_cabinet_action) {
    Q.last_cabinet_action = 0;
}
// reset the timer...
var card = this.game.scenes[this.state.prevTopSceneId];
if (Q[card.id + "_timer"]) {
    Q[card.id + "_timer"] = 0;
}
// reduce visit count
if (this.state.visits[card.id]) {
    this.state.visits[card.id] -= 1;
}

if (Q.month_actions > 0) {
    Q.month_actions -= 1;
}
// return card to hand
var card = this.state.lastPlayedCard;
card.image = card.cardImage;
if (Q.difficulty >= 0) {
    this.state.currentHands['main'].push(card);
} else {
    this.state.currentHands['main.main_easy'].push(card);
}
// reset the timer...
if (Q[card.id + "_timer"]) {
    Q[card.id + "_timer"] = 0;
}
// reduce visit count
if (this.state.visits[card.id]) {
    this.state.visits[card.id] -= 1;
}

if (typeof Q.TIP_banned === 'undefined') {
    Q.TIP_banned = 1;
}
if (typeof Q.disk_endorsement === 'undefined') {
    Q.disk_endorsement = false;
}
// calculate normalized class voting amounts (0 to 100)
for (var c of Q.classes) {
    var class_votes = 0;
    for (var party of Q.parties) {
        if (Q[c+'_'+party] < 0) {
            Q[c+'_'+party] = 0;
        }
        class_votes += Q[c+'_'+party];
    }
    for (var party of Q.parties) {
        Q[c + '_' + party + '_normalized'] = 100*Q[c+'_'+party]/class_votes;
        Q[c + '_' + party + '_display'] = Math.round(100*Q[c+'_'+party]/class_votes);
    }
}

// 1. calculate support for each of the parties
var total_support = 0;
var tipSupport = 0;
for (var party of Q.parties) {
    var party_support = 0;
    for (var c of Q.classes) {
        // but this version is somewhat more entertaining and allows for wilder swings in votes.
        // the other option basically necessitates a multi-class strategy. while the first option allows for a working-class-only strategy.
        if (Q.old_demographics) {
            // okay i'm pretty sure this is wrong, and the hard version is correct
            // basically this allows for "spillover" effects in other demographics.
            // so you can campaign among say the workers and
            party_support += Q[c]*Q[c+'_'+party];
            // However, there should be a cap on how much party support you can get in a certain demo.
        } else  {
            party_support += Q[c]*Q[c+'_'+party+'_normalized'];
        }
    }
    Q[party + '_support'] = party_support;
    total_support += party_support;
    if (party === 'TIP') {
        tipSupport = party_support;
    }
}
// 2. normalize support (fraction)
// 3. Round to integers
// TODO: round to 1 decimal instead of rounding to integers
// to do that, we multiply party_normalized*1000 and divide the rounded number by 10.
var normalization_total = total_support;
var tip_transfer_support = 0;
if (Q.TIP_banned && tipSupport > 0) {
    if (Q.disk_endorsement) {
        tip_transfer_support = tipSupport / 2;
    }
    Q.chp_support = (Q.chp_support || 0) + tip_transfer_support;
    Q.TIP_support = 0;
    normalization_total = total_support - tipSupport + tip_transfer_support;
}
if (normalization_total <= 0) {
    normalization_total = 1;
}
for (var party of Q.parties) {
    if (Q.TIP_banned && party === 'TIP') {
        Q[party+'_normalized'] = 0;
        Q[party+'_votes_dec'] = 0;
        Q[party+'_votes'] = 0;
        Q[party + '_votes_disp'] = Q.use_decimals ? 0 : '0.0';
        continue;
    }
    Q[party+'_normalized'] = Q[party + '_support']/normalization_total;
    Q[party+'_votes_dec'] = Math.round(Q[party+'_normalized']*1000)/10;
    if (Q.use_decimals) {
        Q[party+'_votes'] = Q[party+'_votes_dec'];
        Q[party + '_votes_disp'] = Q[party+'_votes'];
    } else {
        Q[party+'_votes'] = Math.round(Q[party+'_normalized']*100);
        Q[party + '_votes_disp'] = Q[party+'_votes_dec'].toFixed(1);
    }
}

var SYSTEMIC_CRISIS_YEAR_THRESHOLD = 1978;
var FOREX_THRESHOLD_LOW = 40;
var FOREX_THRESHOLD_MID = 60;
var FOREX_THRESHOLD_HIGH = 80;
var FOREX_DISABLE_THRESHOLD = 70;

// Recalculate economic values from base + modifier to ensure modifiers are applied
Q.inflation = (Q.base_inflation || 0) + (Q.inflation_modifier || 0);
Q.economic_growth = (Q.base_growth || 0) + (Q.growth_modifier || 0);
Q.unemployed = (Q.base_unemployment || 0) + (Q.unemployment_modifier || 0);

// Calculate Economic Health based on Inflation, Unemployment, and GDP Growth

// Calculate Inflation Score
if (Q.inflation >= 0 && Q.inflation <= 10) {
    Q.inflation_score = 1;
} else if (Q.inflation > 10 && Q.inflation <= 20) {
    Q.inflation_score = 0;
} else if (Q.inflation > 20 && Q.inflation <= 30) {
    Q.inflation_score = -1;
} else if (Q.inflation > 30 && Q.inflation <= 40) {
    Q.inflation_score = -2;
} else if (Q.inflation > 40 && Q.inflation <= 60) {
    Q.inflation_score = -4;
} else if (Q.inflation > 60) {
    Q.inflation_score = -6;
}

// Calculate Unemployment Score
if (Q.unemployed >= 0 && Q.unemployed <= 3) {
    Q.unemployment_score = 1;
} else if (Q.unemployed > 3 && Q.unemployed <= 6) {
    Q.unemployment_score = 0;
} else if (Q.unemployed > 6 && Q.unemployed <= 10) {
    Q.unemployment_score = -1;
} else if (Q.unemployed > 10 && Q.unemployed <= 15) {
    Q.unemployment_score = -2;
} else if (Q.unemployed > 15 && Q.unemployed <= 20) {
    Q.unemployment_score = -3;
} else if (Q.unemployed > 20) {
    Q.unemployment_score = -5;
}

// Calculate GDP Growth Score
if (Q.economic_growth < 0) {
    Q.growth_score = -5;
} else if (Q.economic_growth >= 0 && Q.economic_growth <= 2) {
    Q.growth_score = -2;
} else if (Q.economic_growth > 2 && Q.economic_growth <= 4) {
    Q.growth_score = -1;
} else if (Q.economic_growth > 4 && Q.economic_growth <= 6) {
    Q.growth_score = 0;
} else if (Q.economic_growth > 6 && Q.economic_growth <= 8) {
    Q.growth_score = 1;
} else if (Q.economic_growth > 8) {
    Q.growth_score = 2;
}

// Calculate raw Economic Health (before forex pressure adjustment)
Q.economy_health_raw = Q.inflation_score + Q.unemployment_score + Q.growth_score;

// Calculate Forex Multiplier based on forex pressure
if (Q.forex_pressure < FOREX_THRESHOLD_LOW) {
    Q.forex_multiplier = 1.0;
} else if (Q.forex_pressure >= FOREX_THRESHOLD_LOW && Q.forex_pressure < FOREX_THRESHOLD_MID) {
    Q.forex_multiplier = 0.6;
} else if (Q.forex_pressure >= FOREX_THRESHOLD_MID && Q.forex_pressure < FOREX_THRESHOLD_HIGH) {
    Q.forex_multiplier = 0.35;
} else if (Q.forex_pressure >= FOREX_THRESHOLD_HIGH) {
    Q.forex_multiplier = 0.1;
    Q.crisis_forex_flag = 1;
}

// Apply forex multiplier asymmetrically
if (Q.economy_health_raw >= 0) {
    Q.economy_health = Math.round(Q.economy_health_raw * Q.forex_multiplier);
} else {
    Q.economy_health = Math.round(Q.economy_health_raw / Q.forex_multiplier);
}

// Set health status text for display
if (Q.economy_health > 3) {
    Q.health_economy_status = "Booming";
} else if (Q.economy_health >= 0 && Q.economy_health <= 3) {
    Q.health_economy_status = "Stable";
} else if (Q.economy_health >= -5 && Q.economy_health < 0) {
    Q.health_economy_status = "Unhealthy";
} else if (Q.economy_health < -5) {
    Q.health_economy_status = "In Crisis";
}

// Hard threshold effects
Q.industrial_cards_disabled = (Q.forex_pressure > FOREX_DISABLE_THRESHOLD) ? 1 : 0;
if (Q.economy_health < -7) {
    Q.strike_unrest_risk = 1;
} else {
    Q.strike_unrest_risk = 0;
}
if (Q.economy_health < -10 && Q.year >= SYSTEMIC_CRISIS_YEAR_THRESHOLD) {
    Q.systemic_crisis_mode = 1;
} else if (Q.systemic_crisis_mode && Q.economy_health >= -10) {
    Q.systemic_crisis_mode = 0;
}

// Apply economic effects each turn when flag is set
// Effects are scaled per turn to gradually accumulate over time
if (Q.apply_economy_effects == 1) {

    if (Q.economy_health > 3) {
        // Booming Economy: +0.83% per turn all demographics, +1.67% capitalists (percentages rounded)
        // (equivalent to +5% and +10% over 6 turns / 3 months)
        if (Q.CHP_in_government) {
            Q.rural_chp += 0.833333;
            Q.workers_chp += 0.833333;
            Q.petty_bourgeoisie_chp += 0.833333;
            Q.state_employees_chp += 0.833333;
            Q.capitalists_chp += 1.666667;
            Q.alevis_chp += 0.833333;
            Q.kurds_chp += 0.833333;
        } else if (Q.z_in_government) {
            Q.rural_z += 0.833333;
            Q.workers_z += 0.833333;
            Q.petty_bourgeoisie_z += 0.833333;
            Q.state_employees_z += 0.833333;
            Q.capitalists_z += 1.666667;
            Q.alevis_z += 0.833333;
            Q.kurds_z += 0.833333;
        } else if (Q.DP_in_government) {
            Q.rural_DP += 0.833333;
            Q.workers_DP += 0.833333;
            Q.petty_bourgeoisie_DP += 0.833333;
            Q.state_employees_DP += 0.833333;
            Q.capitalists_DP += 1.666667;
            Q.alevis_DP += 0.833333;
            Q.kurds_DP += 0.833333;
        }
    } else if (Q.economy_health >= -5 && Q.economy_health < 0) {
        // Unhealthy Economy: -0.33% per turn all demographics (percentage rounded)
        // (equivalent to -2% over 6 turns / 3 months)
        if (Q.CHP_in_government) {
            Q.rural_chp -= 0.333333;
            Q.workers_chp -= 0.333333;
            Q.petty_bourgeoisie_chp -= 0.333333;
            Q.state_employees_chp -= 0.333333;
            Q.capitalists_chp -= 0.333333;
            Q.alevis_chp -= 0.333333;
            Q.kurds_chp -= 0.333333;
        } else if (Q.z_in_government) {
            Q.rural_z -= 0.333333;
            Q.workers_z -= 0.333333;
            Q.petty_bourgeoisie_z -= 0.333333;
            Q.state_employees_z -= 0.333333;
            Q.capitalists_z -= 0.333333;
            Q.alevis_z -= 0.333333;
            Q.kurds_z -= 0.333333;
        } else if (Q.DP_in_government) {
            Q.rural_DP -= 0.333333;
            Q.workers_DP -= 0.333333;
            Q.petty_bourgeoisie_DP -= 0.333333;
            Q.state_employees_DP -= 0.333333;
            Q.capitalists_DP -= 0.333333;
            Q.alevis_DP -= 0.333333;
            Q.kurds_DP -= 0.333333;
        }
    } else if (Q.economy_health < -5) {
        // Crisis Economy: -0.67% per turn all demographics (percentage rounded)
        // (equivalent to -4% over 6 turns / 3 months)
        if (Q.CHP_in_government) {
            Q.rural_chp -= 0.666667;
            Q.workers_chp -= 0.666667;
            Q.petty_bourgeoisie_chp -= 0.666667;
            Q.state_employees_chp -= 0.666667;
            Q.capitalists_chp -= 0.666667;
            Q.alevis_chp -= 0.666667;
            Q.kurds_chp -= 0.666667;
        } else if (Q.z_in_government) {
            Q.rural_z -= 0.666667;
            Q.workers_z -= 0.666667;
            Q.petty_bourgeoisie_z -= 0.666667;
            Q.state_employees_z -= 0.666667;
            Q.capitalists_z -= 0.666667;
            Q.alevis_z -= 0.666667;
            Q.kurds_z -= 0.666667;
        } else if (Q.DP_in_government) {
            Q.rural_DP -= 0.666667;
            Q.workers_DP -= 0.666667;
            Q.petty_bourgeoisie_DP -= 0.666667;
            Q.state_employees_DP -= 0.666667;
            Q.capitalists_DP -= 0.666667;
            Q.alevis_DP -= 0.666667;
            Q.kurds_DP -= 0.666667;
        }
    }
}

// Turkish demographic groups with accurate percentages
Q.rural = 45; // Rural population
Q.workers = 15; // Industrial workers
Q.petty_bourgeoisie = 15; // Small business owners, artisans
Q.state_employees = 7; // State employees and bureaucrats
Q.capitalists = 3; // Capitalists and land owners
Q.alevis = 10; // Alevi religious minority
Q.kurds = 5; // Kurdish political movement

Q.classes = ['rural', 'workers', 'petty_bourgeoisie', 'state_employees', 'capitalists', 'alevis', 'kurds'];
Q.parties = ['chp', 'TIP', 'z', 'CGP', 'DP', 'MSP', 'MHP', 'other'];
if (typeof Q.disk_endorsement === 'undefined') {
    Q.disk_endorsement = false;
}

// Support for party by social group - Turkish political alignment
// Rural (45% of population)
Q.rural_TIP = 0;
Q.rural_chp = 21;
Q.rural_z = 40; // AP uses 'z' variable
Q.rural_CGP = 3;
Q.rural_DP = 15;
Q.rural_MSP = 15;
Q.rural_MHP = 3;
Q.rural_other = 3;

// Workers (15% of population)
Q.workers_TIP = 0;
Q.workers_chp = 50;
Q.workers_z = 18; // AP
Q.workers_CGP = 2;
Q.workers_DP = 5;
Q.workers_MSP = 12;
Q.workers_MHP = 3;
Q.workers_other = 3;

// Petty Bourgeoisie (15% of population)
Q.petty_bourgeoisie_TIP = 0;
Q.petty_bourgeoisie_chp = 19;
Q.petty_bourgeoisie_z = 36; // AP
Q.petty_bourgeoisie_CGP = 7;
Q.petty_bourgeoisie_DP = 13;
Q.petty_bourgeoisie_MSP = 18;
Q.petty_bourgeoisie_MHP = 5;
Q.petty_bourgeoisie_other = 2;

// State Employees (7% of population)
Q.state_employees_TIP = 0;
Q.state_employees_chp = 47;
Q.state_employees_z = 18; // AP
Q.state_employees_CGP = 23;
Q.state_employees_DP = 5;
Q.state_employees_MSP = 0;
Q.state_employees_MHP = 4;
Q.state_employees_other = 3;

// Capitalists and Land Owners (3% of population)
Q.capitalists_TIP = 0;
Q.capitalists_chp = 7;
Q.capitalists_z = 55; // AP
Q.capitalists_CGP = 8;
Q.capitalists_DP = 25;
Q.capitalists_MSP = 5;
Q.capitalists_MHP = 0;
Q.capitalists_other = 0;

// Alevis (10% of population)
Q.alevis_TIP = 0;
Q.alevis_chp = 66;
Q.alevis_z = 8; // AP
Q.alevis_CGP = 0;
Q.alevis_DP = 1;
Q.alevis_MSP = 0;
Q.alevis_MHP = 1;
Q.alevis_other = 24;

// Kurdish Political Movement (5% of population)
Q.kurds_TIP = 0;
Q.kurds_chp = 33;
Q.kurds_z = 32; // AP
Q.kurds_CGP = 3;
Q.kurds_DP = 2;
Q.kurds_MSP = 3;
Q.kurds_MHP = 0;
Q.kurds_other = 19;

var parties = Array.isArray(Q.parties) ? Q.parties.slice() : ['chp', 'TIP', 'z', 'CGP', 'DP', 'MSP', 'MHP', 'other'];
var classes = Array.isArray(Q.classes) ? Q.classes.slice() : ['rural', 'workers', 'petty_bourgeoisie', 'state_employees', 'capitalists', 'alevis', 'kurds'];
var totalSeats = Q.local_total_seats || Q.total_municipalities || 67;
var threshold = (typeof Q.local_election_threshold !== 'undefined') ? Q.local_election_threshold : (Q.local_threshold || 8);
if (typeof Q.TIP_banned === 'undefined') {
    Q.TIP_banned = 1;
}
if (typeof Q.disk_endorsement === 'undefined') {
    Q.disk_endorsement = false;
}

for (var c of classes) {
    var class_votes = 0;
    for (var party of parties) {
        var key = c + '_' + party;
        if (typeof Q[key] === 'undefined') {
            Q[key] = 0;
        }
        if (Q[key] < 0) {
            Q[key] = 0;
        }
        class_votes += Q[key];
    }
    if (class_votes <= 0) {
        class_votes = 1;
    }
    for (var party of parties) {
        var normalized = 100 * (Q[c + '_' + party] || 0) / class_votes;
        Q[c + '_' + party + '_normalized'] = normalized;
        Q[c + '_' + party + '_display'] = Math.round(normalized);
    }
}

var partySupports = {};
var total_support = 0;
var tipSupport = 0;

for (var party of parties) {
    var party_support = 0;
    for (var c of classes) {
        if (Q.old_demographics) {
            party_support += (Q[c] || 0) * (Q[c + '_' + party] || 0);
        } else {
            party_support += (Q[c] || 0) * (Q[c + '_' + party + '_normalized'] || 0);
        }
    }
    partySupports[party] = party_support;
    Q[party + '_local_support'] = party_support;
    total_support += party_support;
    if (party === 'TIP') {
        tipSupport = party_support;
    }
}

var normalization_total = total_support;
if (Q.TIP_banned && tipSupport > 0) {
    var tip_transfer_support = 0;
    if (Q.disk_endorsement) {
        tip_transfer_support = tipSupport / 2;
    }
    partySupports['chp'] = (partySupports['chp'] || 0) + tip_transfer_support;
    partySupports['TIP'] = 0;
    normalization_total = total_support - tipSupport + tip_transfer_support;
}
if (normalization_total <= 0) {
    normalization_total = 1;
}

var partyData = [];
for (var party of parties) {
    var support = partySupports[party] || 0;
    var normalized = support / normalization_total;
    if (!isFinite(normalized) || normalized < 0) {
        normalized = 0;
    }
    Q[party + '_local_normalized'] = normalized;
    var votes_dec = Math.round(normalized * 1000) / 10;
    if (!isFinite(votes_dec) || votes_dec < 0) {
        votes_dec = 0;
    }
    Q[party + '_local_votes_dec'] = votes_dec;
    Q[party + '_local_votes'] = Q.use_decimals ? votes_dec : Math.round(normalized * 100);
    Q[party + '_local_votes_disp'] = votes_dec.toFixed(1);
    partyData.push({
        party: party,
        normalized: normalized,
        votesPercent: votes_dec,
        seats: 0,
        remainder: 0,
        eligible: false
    });
}

var totalSeatsInt = Math.max(0, Math.round(totalSeats));
if (totalSeatsInt <= 0) {
    totalSeatsInt = 67;
}
Q.local_total_seats = totalSeatsInt;
var thresholdValue = (typeof threshold !== 'undefined' && threshold !== null) ? threshold : 8;
var distributionSeats = Math.max(0, totalSeatsInt - 5);

var allocated = 0;
for (var entry of partyData) {
    if (entry.votesPercent >= thresholdValue) {
        entry.eligible = true;
        var exactSeats = entry.normalized * distributionSeats;
        if (entry.votesPercent < 20) {
            exactSeats = exactSeats / 2;
        }
        if (!isFinite(exactSeats) || exactSeats < 0) {
            exactSeats = 0;
        }
        entry.seats = Math.floor(exactSeats);
        entry.remainder = exactSeats - entry.seats;
    } else {
        entry.eligible = false;
        entry.seats = 0;
        entry.remainder = 0;
    }
    Q[entry.party + '_local_seats'] = entry.seats;
    allocated += entry.seats;
}

var remaining = distributionSeats - allocated;

function awardSeat(target) {
    if (!target) {
        return false;
    }
    target.seats += 1;
    target.remainder = Math.max(0, target.remainder - 1);
    Q[target.party + '_local_seats'] = target.seats;
    return true;
}

while (remaining > 0) {
    var best = null;
    for (var entry of partyData) {
        if (!entry.eligible || entry.remainder <= 0) {
            continue;
        }
        if (!best || entry.remainder > best.remainder + 1e-6 || (Math.abs(entry.remainder - best.remainder) <= 1e-6 && entry.normalized > best.normalized)) {
            best = entry;
        }
    }
    if (!best) {
        break;
    }
    awardSeat(best);
    remaining -= 1;
}

if (remaining > 0) {
    var eligible = partyData.filter(function(entry) { return entry.eligible; });
    eligible.sort(function(a, b) {
        if (b.normalized !== a.normalized) {
            return b.normalized - a.normalized;
        }
        return b.votesPercent - a.votesPercent;
    });
    var idx = 0;
    while (remaining > 0 && eligible.length > 0) {
        var target = eligible[idx % eligible.length];
        awardSeat(target);
        remaining -= 1;
        idx += 1;
    }
}

Q.local_seats_unallocated = Math.max(0, remaining);

var bonusTarget = null;
for (var entry of partyData) {
    if (!bonusTarget || entry.normalized > bonusTarget.normalized) {
        bonusTarget = entry;
    }
}
if (bonusTarget) {
    bonusTarget.seats += 5;
    Q[bonusTarget.party + '_local_seats'] = bonusTarget.seats;
}

var largestKey = null;
var secondKey = null;
var largestSeats = -1;
var secondSeats = -1;
var largestShare = -1;
var secondShare = -1;

for (var entry of partyData) {
    var seats = entry.seats;
    var share = entry.normalized;
    if (seats > largestSeats || (seats === largestSeats && share > largestShare)) {
        secondSeats = largestSeats;
        secondShare = largestShare;
        secondKey = largestKey;
        largestSeats = seats;
        largestShare = share;
        largestKey = entry.party;
    } else if (seats > secondSeats || (seats === secondSeats && share > secondShare)) {
        secondSeats = seats;
        secondShare = share;
        secondKey = entry.party;
    }
}

Q.local_largest_party_key = largestKey;
Q.local_second_party_key = secondKey;

var chpSeats = Q.chp_local_seats || 0;
Q.municipalities_controlled = chpSeats;
Q.total_municipalities = totalSeatsInt;

if (typeof updateMunicipalityControlLevel === 'function') {
    updateMunicipalityControlLevel();
} else if (typeof getMunicipalityControlLevel === 'function') {
    Q.municipality_control_level = getMunicipalityControlLevel();
}

Q.last_local_election_year = Q.year;
Q.last_local_election_month = Q.month;
Q.last_local_election_week = Q.week;

onDisplay.$code:
var mods_list = document.getElementById('mods_list');
// load mods from url
var mods_url = 'https://aucchen.github.io/social_democracy_mods/mod_list.tsv';
var table = document.createElement('table');
table.style.border = '1px solid';
table.style['border-collapse'] = 'collapse';
table.style['font-size'] = '0.8em';
Q.mods_table = {};
fetch(mods_url)
.then(response => response.text())
.then(text => {
    // split text
    var lines = text.split('\n');
    for (var i = 0; i < lines.length; i++) {
        var line_data = lines[i].split('\t');
        if (line_data.length < 5) {
            continue;
        }
        var tr = document.createElement('tr');
