// Function to calculate municipality control level dynamically
function getMunicipalityControlLevel() {
    var controlled = Q.municipalities_controlled;
    if (controlled <= 13) {
        return 0; // Terrible
    } else if (controlled <= 26) {
        return 1; // Poor
    } else if (controlled <= 40) {
        return 2; // Divided
    } else if (controlled <= 50) {
        return 3; // Satisfactory
    } else {
        return 4; // Overwhelming
    }
}

// Set initial municipality control level
Q.municipality_control_level = getMunicipalityControlLevel();

// Function to update municipality control level (call this whenever municipalities_controlled changes)
function updateMunicipalityControlLevel() {
    Q.municipality_control_level = getMunicipalityControlLevel();
}

